mapper.select:
select distinct q.id as qid, q.title, q.description, q.gmt_create, q.gmt_modified, q.creator, q.comment_count, q.view_count, q.like_count, q.tag, u.avatar_url, u.name, u.id as uid from question as q inner join user as u on q.creator=u.account_id
and title like concat('%',#{keyword},'%')
and account_id=#{accountId}
order by q.gmt_create desc
limit #{currentPage},#{pageSize}
